/*hide submit button*/
#brxe-kklaqk div.submit-button-wrapper {
  display: none;
}

/* reset list */
#brxe-kklaqk .options-wrapper {
  list-style: none;
  margin: 0;
  padding: 0;
  display: flex;
  flex-direction: column;
  gap: 16px;
}

/* hide native radio buttons*/
#brxe-kklaqk .options-wrapper input[type="radio"] {
  position: absolute;
  opacity: 0;
  pointer-events: none;
}

/* option card */
#brxe-kklaqk .options-wrapper li {
  position: relative;
}

/* label as card */
#brxe-kklaqk .options-wrapper label {
  display: flex;
  flex-direction: column;
  gap: 6px;
  padding: 18px 20px 18px 52px;
  border: 2px solid #e5e5e5;
  border-radius: 8px;
  cursor: pointer;
  font-size: 16px;
  font-weight: 600;
  transition: all 0.25s ease;
  position: relative;
  background: #fff;
}

/* first option price text */
#brxe-kklaqk .options-wrapper label::after {
  content: "$38 per item";
  font-size: 14px;
  line-hight: 20px;
  letter-spacing: 0;
  font-weight: 400;
  color: #525252;
}

/* second option different price text */
#brxe-kklaqk .options-wrapper li:nth-child(2) label::after {
  content: "$38.00 per delivery";
}

/* custom radio */
#brxe-kklaqk .options-wrapper label::before {
  content: "";
  width: 18px;
  height: 18px;
  border: 2px solid #bbb;
  border-radius: 50%;
  position: absolute;
  left: 20px;
  top: 22px;
  background: #fff;
  transition: all 0.25s ease;
}

/* radio dot */
#brxe-kklaqk .options-wrapper label span.radio-dot {
  display: none;
}

/* checked state */
#brxe-kklaqk .options-wrapper input[type="radio"]:checked + label {
  border-color: #000;
}

#brxe-kklaqk .options-wrapper input[type="radio"]:checked + label::before {
  border-color: #000;
  background: radial-gradient(circle at center, #000 45%, transparent 46%);
}

/* hover - active border*/
#brxe-kklaqk .options-wrapper label:hover {
  border-color: #999;
}

#brxe-kklaqk .options-wrapper label:active {
  border-color: #999;
}

/* discount badge */
#brxe-kklaqk .options-wrapper li:nth-child(2)::after {
  content: "15% OFF";
  position: absolute;
  top: 18px;
  right: 18px;
  background: #000;
  color: #fff;
  font-size: 12px;
  line-height: 16px;
  letter-spacing: 0.6px;
  font-weight: 400;
  padding: 4px 8px;
  border-radius: 4px;
}

/* product image */
#brxe-167d6a div.product-img-container figure a {
  display: block;
  overflow: hidden;
  aspect-ratio: 1 / 1;
}

#brxe-167d6a div.product-img-container figure a img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform .35s ease;
}

/* zoom on hover */
#brxe-167d6a:hover figure img {
  transform: scale(1.05);
}


/*add to cart button*/
/* add to cart hidden */
#brxe-167d6a div.add-cart-button-wrapper {
    transform: translateX(-50%);
    opacity: 0;
    transition: opacity .3s ease, transform .3s ease;
}

/* add to cart show on hover */
#brxe-167d6a:hover div.add-cart-button-wrapper {
     opacity: 1;
    transform: translateX(-50%) translateY(-5px);
}

